import React, {useEffect, useState} from 'react';
import {Link, useNavigate, useParams} from 'react-router-dom';
import axios from 'axios';
import Button from '@mui/material/Button';

const Pet = (props) => {
    const {id} = useParams();
    const navigate = useNavigate();

    const [pet, setPet] = useState({})

    useEffect(()=>{
        axios.get(`http://localhost:8000/api/pets/${id}`)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            setPet(res.data);
        })
        .catch((err)=>console.log(err))
    },[])

    const deletePet = () =>{
        axios.delete(`http://localhost:8000/api/pets/${id}`)
        .then((res)=>{
            console.log(res.data);
            navigate("/");
        })
        .catch((err)=>console.log(err))
    }

    const like = () =>{

        axios.put(`http://localhost:8000/api/pets/${id}`,{title, price, desc})
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/");
        })
        .catch((err)=>console.log(err))
    }

    return(
        <div>
            <p>Details about: {pet.name}</p>
            <Link to="/" onClick={() => deletePet(pet._id)}><Button variant="contained" color="error">Adopt {pet.name}</Button></Link>
            <div>
                <table>
                    <tr>
                        <td>Pet type:</td>
                        <td>{pet.type}</td>
                    </tr>
                    <tr>
                        <td>Description</td>
                        <td>{pet.description}</td>
                    </tr>
                    <tr>
                        <td>Skills:</td>
                        <td>
                            <p>{pet.skill1}</p>
                            <p>{pet.skill2}</p>
                            <p>{pet.skill3}</p>
                        </td>
                    </tr>
                </table>
                <Button variant="contained" color="success">Like {pet.name}</Button><span>{pet.likes} like(s)</span>

            </div>
            <Link to="/" onClick={() => deletePet(pet._id)}><button>Delete</button></Link>
        </div>
    )
}

export default Pet;
