import React, {useEffect, useState} from 'react';
import {Link, useNavigate, useParams} from 'react-router-dom';
import axios from 'axios';

const Pet = (props) => {
    const {id} = useParams();
    const navigate = useNavigate();

    const [pet, setPet] = useState({})

    useEffect(()=>{
        axios.get(`http://localhost:8000/api/pets/${id}`)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            setPet(res.data);
        })
        .catch((err)=>console.log(err))
    },[])

    const deletePet = () =>{
        axios.delete(`http://localhost:8000/api/pets/${id}`)
        .then((res)=>{
            console.log(res.data);
            navigate("/");
        })
        .catch((err)=>console.log(err))
    }

    return(
        <div>
            <h1>Pet Shelter</h1>
            <p>Details about: ${pet.name}</p>
            <Link to="/" onClick={() => deletePet(pet._id)}><button>Adopt ${pet.name}</button></Link>
            <div>
                <table>
                    <tr>
                        <td>Pet type:</td>
                        <td>${pet.type}</td>
                    </tr>
                    <tr>
                        <td>Description</td>
                        <td>${pet.description}</td>
                    </tr>
                    <tr>
                        <td>Skills:</td>
                        <td>
                            <p>${pet.skill1}</p>
                            <p>${pet.skills[1]}</p>
                            <p>${pet.skills[2]}</p>
                        </td>
                    </tr>
                </table>
                <button>Like ${pet.name}</button><span>${pet.likes} like(S)</span>

            </div>
            <Link to="/" onClick={() => deletePet(pet._id)}><button>Delete</button></Link>
        </div>
    )
}

export default Pet;
